import React from 'react';
import PropTypes from 'prop-types';
import './Movie.css'
import { Link } from 'react-router-dom';

function Movie({ title, year, summary, poster, genres, rating }) {
    return (
        <div className='movie'>
            <Link to = {'/movie-detail'} state={{ year,title,summary, poster, genres,rating }}> {/* v6버전 변경사항 */}
                <img src={poster} alt={title} title={title} />
                <div className= "movie__data"> 
                    <h3 className="movie__title">{title}</h3>
                    <h4 className="movie__rating">{rating}</h4>
                    <h5 className="movie__year">{year}</h5>
                    <ul className='movie_genres'>
                        {genres.map((genre, index) => {
                            return <li key={index} className='movie__genre'>{genre}</li>;
                        })}
                    </ul>
                    <p className="movie__summary">{summary.slice(0,180)}...(생략)</p>
                </div>
            </Link>
        </div>
    );
}

Movie.propTypes = { 
    year : PropTypes.number.isRequired, // year도 숫자
    rating : PropTypes.number.isRequired,
    title : PropTypes.string.isRequired,
    summary :PropTypes.string.isRequired,
    poster : PropTypes.string.isRequired, // 이미지 주소 저장
    genres : PropTypes.array.isRequired,
};

export default Movie;
