import React,{ useState } from "react";
import './Pagination.css'

const Pagination = ({ postsPerPage, totalPosts, paginate, currentPage }) => {

    const pageNumbers = []; 
    const pageGroup = Math.ceil(totalPosts / postsPerPage);
    const [currentPage1, setCurrentPage1] = useState(currentPage);

    console.log(pageNumbers);

    for (let i = 1; i <= pageGroup; i++) {
        pageNumbers.push(i); // 배열에 값 추가하여 원본 변경
    }

    // console.log(pageNumbers.length)
    console.log(currentPage);

    const prevPageHandler = (a) => {
        if(currentPage !== 1) {
            const a = currentPage-1
            this.setState({ currentPage : a });
            console.log('a',a);
        }
    }


    return (
        <div>
        <nav>
            <ul className="pagination">
            <li onClick={prevPageHandler}> 이전 </li>
            {pageNumbers.map((number) => (
                <li key={number} onClick={()=> paginate(number)} 
                    className={currentPage === number ? "page-item-active" : "page-item"}>
                    <span className="page-link">
                        {number}
                    </span>
                </li>
            ))}
            <li>다음 </li>    

            </ul>
        </nav>
        </div>
    );
};

export default Pagination;
