import React from 'react';
import axios from "axios";
import Albums from "../components/Albums"
import './Home.css';
import Pagination from "../components/Pagination"


class Home extends React.Component {
    state = {
        albums: [],
        currentPage : 1,
        postsPerPage : 5,
    };

    setCurrentPage = page => {
        // this.setState({ currentPage : page });
        this.setState(() => ({
            currentPage : page
        }))
    };

    getAlbums = async () => {
        // const albums = await axios.get('https://jsonplaceholder.typicode.com/albums');
        const { data } = await axios.get(
            "https://jsonplaceholder.typicode.com/albums"
            );

            this.setState({ albums: data });
        };

        componentDidMount() {
            this.getAlbums();

    }


    render() {

        const { albums } = this.state;
        const currentPage  = this.state.currentPage;
        // console.log('currentPage' , currentPage);

        const postsPerPage = this.state.postsPerPage;
        const indexOfLast = currentPage * postsPerPage;
        const indexOfFirst = indexOfLast - postsPerPage;

        const currentPosts = (albums) => {
            let currentPosts = 0;
            currentPosts = albums.slice(indexOfFirst,indexOfLast);
            return currentPosts;
        }
        // console.log('albums : ', albums); //100개 데이터

        // console.log('totalPosts : ', albums.length); //100개 데이터
        return (
            <div className='albums'>
                {currentPosts(albums.map(album => (
                    <Albums 
                        key = {album.id}
                        title = {album.title}
                        id = {album.id}
                        img = {'https://images.saymedia-content.com/.image/t_share/MTc0NDkxNzgyMzYzNDg5NjQw/vinyl-to-paper-how-to-write-an-album-review.jpg'}
                    />
                )))}
                <Pagination 
                    postsPerPage ={postsPerPage}
                    totalPosts={albums.length}
                    paginate={this.setCurrentPage}
                    currentPage={currentPage}
                /> 
            </div>


        );
    }
    }

export default Home;
