import React from "react";
import './Pagination.css'

const Pagination = ({ postsPerPage, totalPosts, paginate, currentPage, prevPage, nextPage }) => {
    const pageNumbers = []; 
    const pageGroup = Math.ceil(totalPosts / postsPerPage);


    for (let i = 1; i <= pageGroup; i++) {
        pageNumbers.push(i); // 배열에 값 추가하여 원본 변경
    }


    // console.log(pageNumbers.length)
    console.log(currentPage);

    // const handleNextbtn = () => {
    //     setcurrentPage(currentPage + 1);

    //     if (currentPage + 1 > maxPageNumberLimit) {
    //       setmaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
    //       setminPageNumberLimit(minPageNumberLimit + pageNumberLimit);
    //     }
    //   };

    return (
        <div>
        <nav>
            <ul className="pagination">
            <li onClick={prevPage} disable={currentPage ===1}> 이전 </li>
            {pageNumbers.map((number) => (
                <li key={number} onClick={()=> paginate(number)} 
                    className={currentPage === number ? "page-item-active" : "page-item"}>
                    <span className="page-link">
                        {number}
                    </span>
                </li>
            ))}
            <li onClick={nextPage}>다음 </li>    

            </ul>
        </nav>
        </div>
    );
};

export default Pagination;
