import React from 'react'; 

class Paging extends React.Component{
    state = {
        page : 1,
        start : 0,
        end : 5
    };

    handleChangeIndexUp = () => {
        const { page, start, end } = this.state;
        this.setState({
            page : page +1 ,
            start : start + 5,
            end : end + 5,
        });
    };

    handleChangeIndexDown = () => {
        const { page, start, end } = this.state;
        this.setState({
            page : page  -1 ,
            start : start - 5,
            end : end - 5,
        });
    };
}

export default Paging
