import React from "react";
import './Pagination.css'

const Pagination = ({ postsPerPage, totalPosts, paginate, currentPage, prevPage, nextPage }) => {
    const pageNumbers = []; 
    const pageGroup = Math.ceil(currentPage/postsPerPage);

    const a = Math.ceil(totalPosts / postsPerPage); // 원조 for문
    const last = pageGroup * postsPerPage; // 화면에 보여질 마지막 페이지 번호
    const first = last - (postsPerPage - 1);

    for (let i = 1; i <= a; i++) {
        console.log('pageGroup:',pageGroup);
        pageNumbers.push(i); // 배열에 값 추가하여 원본 변경
    }

    for(let j = first ; j <= last; j++){
        console.log('j', j)
        if(currentPage === j) {
        }
    }
    console.log('length : ', pageNumbers.length) // page 마지막 값
    console.log('currentPage',currentPage); 

    return (
        <div>
        <nav>
            <ul className="pagination">
            <li onClick={prevPage} className={currentPage === 1 ? "disabled" : "abled"}> 이전 </li>
            {pageNumbers.map((number) => ( 
                <li key={number} onClick={()=> paginate(number)} 
                    className={currentPage === number ? "page-item-active" : "page-item"}>
                    <span className="page-link">
                        {number}
                    </span>
                </li>
            ))}
            <li onClick={nextPage} className={currentPage === pageNumbers.length ? "disabled" : "abled"}>다음 </li>    

            </ul>
        </nav>
        </div>
    );
};

export default Pagination;
